CREATE ALGORITHM=UNDEFINED DEFINER=`root`@`localhost` SQL SECURITY DEFINER VIEW `product_details` AS select `products`.`product_id` AS `product_id`,`products`.`product` AS `product`,`products`.`category_id` AS `category_id`,`products`.`cost_per_unit` AS `cost_per_unit`,`products`.`owner_id` AS `owner_id`,`products`.`create_date` AS `create_date`,`products`.`modified_date` AS `modified_date`,`products`.`active` AS `active`,`products`.`suspended` AS `suspended`,`owner`.`user_id` AS `user_id`,`owner`.`active` AS `owner_active`,`tbl_users`.`suspended` AS `owner_suspended`,`categories`.`category_name` AS `category_name`,`categories`.`measuring_unit` AS `unit`,`categories`.`suspended` AS `category_suspended`,(case when (`products`.`suspended` = 1) then 'Product Deactivated by Admin' when (`products`.`active` = 1) then 'Product Active' else 'Product Deactivated by Owner' end) AS `status` from (((`products` join `owner` on((`products`.`owner_id` = `owner`.`owner_id`))) join `categories` on((`categories`.`category_id` = `products`.`category_id`))) join `tbl_users` on((`tbl_users`.`user_id` = `owner`.`user_id`)))